class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size();
        vector<int> pre(n+1);
        int st = 0;
        for(int i = 1;i<n;i++){
            if(col[i] != col[(i-1+n)%n]) st = i;
            else{
                if(st-k+2 >=0 ) pre[st-k+2]++;
                else{
                    pre[0]++;
                    pre[st-k+2+n]++;
                }
                pre[i]--;
            }
        }
        if(col[0]==col[n-1]){
            pre[n-k+1]++;
        }
        int ans = 0;
        if(pre[0]==0) ans++;
        for(int i = 1;i<n;i++){
            pre[i] += pre[i-1];
            if(pre[i]==0) ans++;
        }
        return ans;
    }
};
