class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        res = 0
        n_half = len(colors)
        colors = colors * 2
        l,r = 0,1
        n = len(colors)
        while r < n:
            if colors[r] != colors[r-1]:
                r += 1
            else:
                l = r
                r += 1

            if r - l >= k and r - k < n_half:
                res += 1
                # print(l,r,k,n_half)

        return res
