class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        arr.insert(arr.end(),arr.begin(),arr.begin()+k);
        unordered_map<string,int>ind;
        int size = arr.size();
        vector<int>temp(size+1,0);
        int cnt=0;

        for(int i=1;i<size;i++)temp[i]=temp[i-1]+temp[i]+abs(arr[i]-arr[i-1]);

        k=k-1;

        for(int i=k+1;i<size;i++)if(temp[i]-temp[i-k]==k)cnt++;
        return cnt;
    }
};
