class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0, c = 1;
        for(int i=0;i<k-1;++i)colors.emplace_back(colors[i]);
        for(int i=1;i<n+k-1;++i){
            if(colors[i]!=colors[i-1])c++;
            else{
                if(c>=k)ans += c-k+1;
                c = 1;
            }
        }
        if(c>=k)ans += c-k+1;
        return ans;
    }
};
