class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i = 0; i<k-1; i++){
            v.push_back(v[i]);
        }
        int ct = 0;
        int ct1 = 1;
        int n = v.size();
        for(int j = 1; j<n; j++){
            if(v[j] != v[j-1]) {
                ct1++;
            } else {
                if(ct1>=k) ct += ct1-k+1;
                ct1 = 1;
            }
        }
       if(ct1>=k) ct += ct1-k+1;
       //for(int i= 0; i<n; i++) cout<<v[i]<<" ";
        return ct;

    }
};
