class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int i=0;
        int j=0;
        int ans=0;
        int count=1;
        bool found=false;
        while(i<n)
        {
            // cout<<i<<" "<<j<<"\n";
            if(count<k)
            {
                int ind=((j+1)<n) ? j+1 : 0;
                if(colors[j]==colors[ind])
                {
                    if(found && j<n)
                    {
                        break;
                    }
                    i=j+1;
                    j=j+1;
                    if(j==n)
                    {
                        j=0;
                        found=true;
                    }
                    count=1;
                }
                else
                {
                    j++;
                    if(j==n)
                    {
                        j=0;
                        found=true;
                    }
                    count++;
                }
            }
            else
            {
                // cout<<i<<" "<<j<<"\n";
                ans++;
                int ind=((j+1)<n) ? j+1 : 0;
                if(colors[j]==colors[ind])
                {
                    if(found && j<n)
                    {
                        break;
                    }
                    i=j+1;
                    j=j+1;
                    if(j==n)
                    {
                        j=0;
                        found=true;
                    }
                    count=1;
                }
                else
                {
                    j++;
                    i++;
                    if(j==n)
                    {
                        j=0;
                        found=true;
                    }
                }
            }
        }
        return ans;
    }
};
