class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        vector<int>dp(2*n,1);
        for(int i=1;i<2*n;i++){
            if(colors[i]!=colors[i-1]){
                dp[i]+=dp[i-1];
            }
        }
        // int count=0;
        for(int i=k;i<k+n;i++){
            cout<<dp[i]<<" ";
            if(dp[i]>=k){
                count++;
            }
        }

        return count;
    }
};
