class Solution {
    public int numberOfAlternatingGroups(int[] arr, int targetLength) {
        int[] extendedArr = new int[arr.length + targetLength - 1];
        System.arraycopy(arr, 0, extendedArr, 0, arr.length);
        for (int i = 0; i < targetLength - 1; i++) {
            extendedArr[arr.length + i] = arr[i];
        }

        int result = 0;
        for (int i = 0; i < extendedArr.length; i++) {
            int sequenceLength = 1;
            for (int j = i + 1; j < extendedArr.length; j++) {
                if (extendedArr[j] != extendedArr[j - 1]) {
                    sequenceLength++;
                } else {
                    i = j - 1;
                    break;
                }
                if (j == extendedArr.length - 1) {
                    i = 3 * extendedArr.length;
                    break;
                }
            }
            result += Math.max(0, sequenceLength - targetLength + 1);
        }
        return result;
    }
}
