class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = size(arr), res = 0, alt = 0, i = 0;
        function<int(int, int)> solve2 = [&](int start, int end) -> int {
            int count = 0;
            for (int i = start; i < end; ++i) {
                count += (arr[i] != arr[i + 1]) ? 1 : 0;
            }
            return count;
        };
        function<void(int, int&)> solve = [&](int idx, int& alt) {
            if (arr[(idx - k) % n] != arr[(idx - k + 1) % n]) alt--;
            if (arr[(idx - 1) % n] != arr[idx % n]) alt++;
        };

        alt = solve2(0, k - 1);
        if (alt + 1 == k) res++;
        for (i = k; i - n - k + 1 < 0; ++i) {
            solve(i, alt);
            res += (alt != k - 1) ? 0 : 1;
        }

        return res;
    }
};
