class Solution:
    def countSubarrays(self, nums: List[int], target: int) -> int:
        tmp = [[0, nums[0]]]
        ans = 0
        if nums[0] == target:
            ans += 1
        n = len(nums)
        for idx in range(1, n):
            tmp.append([idx, nums[idx]])
            v = nums[idx]
            k = len(tmp)
            for i in range(k - 1, -1, -1):
                v &= tmp[i][1]
                tmp[i][1] = v
            ntmp = []
            for i in range(k):
                if ntmp and ntmp[-1][1] == tmp[i][1]: continue
                ntmp.append(tmp[i])
            tmp = ntmp
            k = len(tmp)
            for i in range(k - 1):
                if tmp[i][1] == target:
                    ans += tmp[i+1][0] - tmp[i][0]
            if tmp[-1][1] == target:
                ans += idx + 1 - tmp[-1][0]
        return ans
