using ll = long long;
int f[32], f1[32], g[32], g1[32];
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll ret = 0;
        int n = 0, m = 0;
        for(int i=0; i<a.size(); ++i){
            int v = a[i];
            m = 0;
            g[m] = v;
            g1[m] = i;
            ++m;
            for(int j=0; j<n; ++j){
                int w = v & f[j];
                if(w == g[m-1]){
                    g1[m-1] = f1[j];
                } else {
                    g[m] = w;
                    g1[m++] = f1[j];
                }
            }
            swap(f, g);
            swap(f1, g1);
            swap(n, m);
            for(int j=0; j<n; ++j){
                if(f[j] == k){
                    if(j == 0)  ret += (i - f1[j] + 1);
                    else  ret += (f1[j-1] - f1[j]);
                }
            }
        }
        return ret;
    }
};
