class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> presult;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++ans;
            }
            curr[nums[i]] = 1;

            for (auto& [val, freq] : presult) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    ans += freq;
                }
                curr[newAndResult] += freq;
            }

            presult = curr;
        }

        return ans;
    }
};
