class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int currAnd = 0xFFFFFFFF;
        unordered_map<int, int> andCount;

        for (int num : nums)
        {
            if (num == k)
            {
                ans++;
            }

            unordered_map<int, int> newCount;
            for (auto& [key, value] : andCount)
            {
                int newAnd = key & num;
                if (newAnd == k) {
                    ans += value;
                }
                newCount[newAnd] += value;
            }

            newCount[num]++;
            andCount = newCount;
        }

        return ans;
    }
};
