/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
        let length = nums.length
        let total = 0
        let pre = new Map()

        for (let i = 0; i < length; ++i) {   
            let cur = new Map()
            if (nums[i] == k) {
                ++total;
            }
            cur.set(nums[i], 1)
            for(let [val, fre] of pre.entries())
                {
                     let newAndResult = val & nums[i]
                     if (newAndResult == k) {
                            total += fre;
                        }
                        cur.set(newAndResult, (cur.get(newAndResult) || 0) +fre)

                }
            pre = cur 
        }
        return total;
};
