from collections import defaultdict

class Solution:
    def countSubarrays(self, array, target):
        n = len(array)
        t = 0
        p = defaultdict(int)
        for i in range(n):
            c = defaultdict(int)
            if array[i] == target:
                t += 1
            c[array[i]] = 1
            for v, f in p.items():
                a = v & array[i]
                if a == target:
                    t += f
                c[a] += f
            p = c
        return t
