class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        map<long long,long long> mp;
        set<long long> s;
        long long ans = 0;
        for(int i=0;i<nums.size();i++){

            long long y = nums[i];
            set<long long> t;
            map<long long,long long> ok;
            for(long long x:s){

                long long temp = x&y;
                //cout<<y<<" "<<temp<<endl;
                if(temp==k)ans+=mp[x];
                t.insert(temp);
                ok[temp] += mp[x];
            }
            if(y==k)ans++;
            ok[y]++;
            t.insert(y);
            s=t;
            mp=ok;
            //cout<<ans<<endl;
        }
        return ans;
    }
};
