class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int length = arr.size();
        long long totalSubarrays = 0;

        unordered_map<long long, long long> previousMap;

        for (int idx = 0; idx < length; ++idx) {
            unordered_map<long long, long long> currentMap; 

            if (arr[idx] == target) {
                ++totalSubarrays;
            }
            currentMap[arr[idx]] = 1;

            for (auto it = previousMap.begin(); it != previousMap.end(); ++it) {
                long long newAndValue = it->first & arr[idx];
                if (newAndValue == target) {
                    totalSubarrays += it->second;
                }
                currentMap[newAndValue] += it->second;
            }

            previousMap = currentMap;
        }

        return totalSubarrays;
    }
};
