class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        check = [False] * 31
        last_zeros = [-1] * 31
        for i in range(31):
            check[i] = (k >> i) & 1

        ans = 0
        for i, num in enumerate(nums):
            for j in range(31):
                if not ((num >> j) & 1):
                    last_zeros[j] = i
            start_min, start_max = 0, i
            for j in range(31):
                if check[j]:
                    start_min = max(start_min, last_zeros[j] + 1)
                else:
                    start_max = min(start_max, last_zeros[j])
            ans += max(0, start_max - start_min + 1)

        return ans
