class Solution {
    public long countSubarrays(int[] nums, int k) {
        int[] zero = new int[32], one = new int[32];
        Arrays.fill(zero, -1);
        Arrays.fill(one, 0);
        long ans = 0;

        for (int i = 0; i < nums.length; i++)
        {
            int l = 0, r = i;
            for (int j = 0; j < 32; j++)
            {
                int b = (k >> j) & 1, c = (nums[i] >> j) & 1;
                if (c == 0)
                {
                    zero[j] = i;
                    one[j] = i + 1;
                }

                if (b == 1) l = Math.max(l, one[j]);
                else r = Math.min(r, zero[j]);
            }
            ans += Math.max(0, r - l + 1);
        }
        return ans;
    }
}
