class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long,long long> mp;
        long long ans = 0;
        for(auto it : nums) {
            if(it==k) ans++;
            unordered_map<long long,long long> temp; 
            temp[it]++;
            for(auto it1 : mp){
                int x = it1.first&it;
                if(x==k) ans += it1.second;
                if (temp.find(x)==temp.end()) temp[x] = it1.second;
                else temp[x] += it1.second;
            }
            mp = temp;
        }
        return ans;
    }
};
