class Solution {
public:
long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long count = 0;
        map<long long, long long> prev, curr;
        long long res;

        for (auto num : a) {
            curr.clear();
            if (num == k)count++;
            curr[num]++;

            for (auto [val, occ] : prev) {
                res = val & num;
                if (res == k)count += occ;
                curr[res] += occ;
            }

            prev = curr;
        }
        return count;
    }
};
