#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        ll ans=0;
        ll n=v.size();

        map<ll,ll> mo,mn;
        for(int i=0;i<n;i++){
            mn.clear();
            mn[v[i]]++;
            for(auto kk: mo){
                mn[v[i]&kk.first]+=kk.second;
            }
            ans+=mn[k];
            mo=mn;
        }

        return ans;
    }
};
