class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0, andd;
        unordered_map<long long, long long> ump1;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> ump2;
            if (k == nums[i]) {
                res++;
            }
            ump2[nums[i]] = 1;
            for (auto& [a, b] : ump1) {
                andd = a & nums[i];
                if (k == andd) {
                    res += b;
                }
                ump2[andd] += b;
            }
            ump1 = ump2;
        }
        return res;
    }
};
