class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        map<int,int> mp;
        for(int i=0;i<n;i++) {
            map<int,int> mp2;
            if((nums[i]&k) == k) {
                mp2[nums[i]]++;
                if(nums[i] == k) ans++;
            }

            for(auto &[key,value] : mp) {
                int newKey = key & nums[i];
                if((newKey & k) == k) {
                    mp2[newKey] += value;
                    if(newKey == k) ans += value;
                }
            }
            mp = mp2;
        }
        return ans;
    }
};
