class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long ,long long> mp;

        long long cnt = 0;
        for(int i = 0; i < nums.size() ;i++)
        {
            unordered_map<long long,long long>  mp1;

            if(nums[i] == k) cnt++;
            mp1[nums[i]] = 1;

            for(auto& [m,l] : mp){
                long long an = m & nums[i];

                if(an == k) cnt += l;

                mp1[an] += l;
            }

            mp = mp1;
        }

        return cnt;
    }
};
