class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long final_answer = 0;
        int temp=k;

         int vector_length = nums.size();


        unordered_map<long long, long long>m_P;

        for (int index_1 = 0; index_1 < vector_length; ++index_1) {
            unordered_map<long long, long long>m_ppP; 

            if (nums[index_1] == temp) {
                final_answer++;
            }
            m_ppP[nums[index_1]] = 1;



           for (auto &it: m_P) {
                long long n_a_r = it.first & nums[index_1];
                if (n_a_r == temp) {
                    final_answer += it.second;
                }
                m_ppP[n_a_r] += it.second;
            }

            m_P = m_ppP;
        }

        return final_answer;

