#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ans=0;
        unordered_map<ll,ll> mp;

        ll psum=0;
        for(int i=0;i<n;i++) {
            unordered_map<ll,ll> temp;

            if(nums[i]==k) {
                ans++;
            }
            temp[nums[i]]++;

            for(auto it:mp) {
                ll res=it.first & nums[i];
                if(res == k) {
                    ans+=it.second;
                }
                temp[res]+=it.second;
            }
            mp=temp;
        }
        return ans;
    }
};
