class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      long long ans=0;
        int n=nums.size();
       unordered_map<long long,long long>mp;
       for(int i=0;i<n;i++){
            unordered_map<long long,long long>mp2;
           if(nums[i]==k) ans++;
          mp2[nums[i]]=1;
           for(auto it:mp){
             long long curr=it.first&nums[i];
             if(curr==k)ans+=it.second;
             mp2[curr]+=it.second;
         }
           mp=mp2;
       } 
      return ans;
    }
};
