class Solution(object):
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0
        subList = {}

        for i in range(n):
            subArrayList = {}

            if nums[i]==k:
                count = count + 1;
            subArrayList[nums[i]] = 1;

            for j, l in subList.items():
                ele = j & nums[i]
                if ele == k:
                    count += l
                if ele in subArrayList:
                    subArrayList[ele] += l
                else:
                    subArrayList[ele] = l

            subList = subArrayList

        return count
