class Solution:
    def countSubarrays(self, nums: List[int], kk: int) -> int:
        l , r = {} , {}
        res = 0
        for i , x in enumerate(nums):                        
            nl , nr = {} , {}      
            if x not in l:
                l[x] = i
            r[x] = i
            for k , v in l.items():
                nl[k & x] = min(nl.get(k & x , inf) , v)
            for k , v in r.items():
                nr[k & x] = max(nr.get(k & x , -1) , v)
            r , l = nr.copy() , nl.copy()
            #print(l , r)
            if kk in l:
                res += r[kk] - l[kk] + 1            
        return res
