func countSubarrays(array []int, target int) int64 {
    size := len(array)
    var total int64
    previousResults := make(map[int]int64)

    for index := 0; index < size; index++ {
        currentResults := make(map[int]int64)
        if array[index] == target {
            total++
        }
        currentResults[array[index]] = 1
        for value, frequency := range previousResults {
            newAndResult := value & array[index]
            if newAndResult == target {
                total += frequency
            }
            currentResults[newAndResult] += frequency
        }
        previousResults = currentResults
    }

    return total
}
