class Solution {
public:
    long long countSubarrays(vector<int>& vals, int a) {
        int n = vals.size();
        long long tot = 0;
        unordered_map<long long, long long> pastval;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 
            if (vals[i] == a) {
                ++tot;
            }
            cr[vals[i]] = 1;

            for (auto& [val, fq] : pastval) {
                long long newval = val & vals[i];
                if (newval == a) {
                    tot += fq;
                }
                cr[newval] += fq;
            }

            pastval = cr;
        }

        return tot;
    }
};
