class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int length = nums.size();
        long long totalSubarrays = 0;
        unordered_map<long long, long long> previousAndResults;
        for (int i = 0; i < length; ++i) {
            unordered_map<long long, long long> currentAndResults; 
            if (nums[i] == target) {
                ++totalSubarrays;
            }
            currentAndResults[nums[i]] = 1;
            for (auto& [andResult, frequency] : previousAndResults) {
                long long newAndResult = andResult & nums[i];
                if (newAndResult == target) {
                    totalSubarrays += frequency;
                }
                currentAndResults[newAndResult] += frequency;
            }

            previousAndResults = currentAndResults;
        }
        return totalSubarrays;
    }
};
