class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ans = 0
        prev = {}
        for i in range(n):
            curr = {}
            if nums[i] == k:
                ans += 1
            curr[nums[i]] = 1
            for val, freq in prev.items():
                newAnd = val & nums[i]
                if newAnd == k:
                    ans += freq
                curr[newAnd] = curr.get(newAnd, 0) + freq
            prev = curr
        return ans
