class Solution:
    def countSubarrays(self, nums, k: int) -> int:
        from collections import defaultdict
        d = defaultdict(int)
        n = len(nums)
        res = 0
        for i in range(n):
            x = defaultdict(int)
            res += 1 if nums[i] == k else 0
            for kk, v in d.items():
                if kk & nums[i] == k:
                    res += v
                x[kk & nums[i]] += v
            x[nums[i]] += 1
            d = x
        return res
