typedef long long ll;
typedef long double ld;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> cur;
        int n = nums.size();
        ll ans = 0LL;
        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> nxt;
            for (auto& [key, val] : cur) {
                nxt[key & nums[i]] += val;
            }
            cur = move(nxt);
            ++cur[nums[i]];
            ans += cur[k];
        }
        return ans;
    }
};
