class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        pcnt = defaultdict(lambda :0)
        ans = 0
        for num in nums:
            cnt = defaultdict(lambda :0)
            cnt[num] = 1
            for i in pcnt:
                AND = i & num
                cnt[AND] += pcnt[i]
                if AND == k:
                    ans += pcnt[i]
            pcnt = cnt
        ans += nums.count(k)
        return ans
