#include<bits/stdc++.h>
#include<ext/pb_ds/assoc_container.hpp>
#include<ext/pb_ds/tree_policy.hpp>
using namespace std;
using namespace __gnu_pbds;
using namespace chrono;

class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        ios_base::sync_with_stdio(false);cin.tie(NULL);cout.tie(NULL);
        int n = v.size();
        long long cnt = 0;
        gp_hash_table<long long, long long> mp;
        for (auto i:v) {
            gp_hash_table<long long, long long> mp1;
            if (i == k) cnt++;
            for (auto p : mp) {
                long long and1 = p.first & i;
                if (and1 == k) cnt += p.second;
                mp1[and1] += p.second;
            }
            mp1[i]++;
            mp = move(mp1);
        }
        return cnt;
    }
};
