class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {


        unordered_map<long long, long long> prevFreqMap;
        int length = nums.size();
        long long result = 0;

        for (int idx = 0; idx < length; ++idx) {

            unordered_map<long long, long long> currFreqMap;

            if (nums[idx] == target) {
                ++result;
            }

            currFreqMap[nums[idx]] = 1;

            for (auto& [prevVal, prevFreq] : prevFreqMap) {
                long long newAndVal = prevVal & nums[idx];
                if (newAndVal == target) {
                    result += prevFreq;
                }
                currFreqMap[newAndVal] += prevFreq;
            }

            prevFreqMap = move(currFreqMap);
        }

        return result;

    }
};
