class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<vector<int>> l1(n, vector<int>(32, 0));
        for(int i=0;i<n;i++) {
            int x = nums[i];
            for(int j=0;j<32;j++){
                if(x&(1<<j)) l1[i][j] = (i>0?l1[i-1][j]:0)+1;
            }
        }
        int b[32];
        for(int i=0;i<32;i++){
            if(k&(1<<i)) b[i] = 1;
            else b[i] = 0;
        }
        for(int i=0;i<n;i++){
            int m1 = i+1, m0 = 0;
            for(int j=0;j<32;j++){
                if(b[j]) {
                    m1 = min(m1, l1[i][j]);
                } else {
                    m0 = max(m0, l1[i][j]);
                }
            }
            //cout << i << ":" << m1 << "," << m0 << endl;
            ans += max(m1-m0, 0);
        }
        return ans;
    }
};
