class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        long long ans= 0;

        unordered_map<int,int> mpp;

        for(int i = 0; i < n; i++){
            unordered_map<int, int> temp;

            for (auto j :  mpp) {
                int value = j.first & nums[i];

                if (value == k)
                {
                    ans = ans + j.second;
                }

                temp[value] += j.second;
            }

            if(nums[i] == k){
                ans++;
            }

            temp[nums[i]]++;
            mpp = temp;
        }
        return ans;
    }
};
