from typing import List

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        s = len(nums)
        t = 0
        p = {}

        for i in range(s):
            c = {}
            x = k

            if nums[i] == x:
                t += 1
            c[nums[i]] = 1

            for v, f in p.items():
                r = v & nums[i]
                if r == x:t += f
                if r in c:c[r] += f
                else:c[r] = f
            p = c

        return t
