class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       long long int n=nums.size();
        long long int c=0;
        unordered_map<long long,long long> p;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long> cr;
            if(nums[i]==k){
                c++;
            }
            cr[nums[i]]=1;
            for(auto x:p){
                long long r=x.first & nums[i];
                if(r==k){
                    c+=x.second;
                }
                cr[r]+=x.second;
            }
            p=cr;
        }
        return c;
    }
};
