class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        size = len(nums)
        total = 0

        prev = defaultdict(int)

        for index in range(size):
            cur = defaultdict(int)

            if nums[index] == k:
                total += 1

            cur[nums[index]] = 1

            for value, frequency in prev.items():
                newres = value & nums[index]
                if newres == k:
                    total += frequency
                cur[newres] += frequency

            prev = cur

        return total
