class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        cnt = defaultdict(int)
        res = 0
        for n in nums:
            tmp = defaultdict(int)
            tmp[n] += 1
            for x in cnt:
                y = x & n
                if y >= k:
                    tmp[y] += cnt[x]

            res += tmp[k]
            cnt = tmp
        return res
