class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;

        map<long long, long long> dp1;
        int n = nums.size();
        for(int i = 0; i < n; i++){
            if(nums[i] == k){
                ans++;
            }
            map<long long, long long> dp2;
            dp2[nums[i]]++;
            for(auto it:dp1){
                auto nxt = it.first & nums[i];
                if(nxt == k){
                    ans+= it.second;
                }
                dp2[nxt] += it.second;
            }

            dp1 = dp2;
        }
        return ans;
    }
};
