class Solution { 
public: 
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 
        unordered_map<long long, long long> prev; 
        long long cnt = 0,temp;

        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> cur;  

            if (nums[i] == k) { 
                ++cnt; 
            } 
            cur[nums[i]] = 1; 

            for (auto& [val, freq] : prev) { 
                temp = val & nums[i]; 
                if (temp == k) { 
                    cnt += freq; 
                } 
                cur[temp] += freq; 
            } 

            prev = cur; 
        } 

        return cnt; 
    } 
};
