class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long total = 1LL * 0; 
        unordered_map<long long, long long> mp; 

        for(auto &ele : nums){ 
            unordered_map<long long, long long> cache;  
            cache[ele] = 1;

            if (ele == k)  
                total++;

            for (auto &it : mp) { 
                if((it.first & ele) == k)
                    total += 1LL * it.second; 
                cache[it.first & ele] += 1LL * it.second; 
            }
            mp = cache; 
        } 
        return total; 
    }
};
