#define ll long long
class Solution {

public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll, ll> last;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> cur; 
            if (nums[i] == k) ++ans;
            cur[nums[i]] = 1;

    // bool fn(vector<vector<int>> &dp , int n, int s, int e, int k){
