    //             if(dp[e][i]-dp[s][i] != (e-s)) return true; 
    //         }
    //     }
    //     return false;
    // }

            for (auto p : last) {
                ll v  = p.first, f = p.second;
                long long a = v & nums[i];
                if (a == k) {
                    ans += f;
                }
                cur[a] += f;
            }

            last = cur;
        }

        return ans;
    }
};
